INSERT OR REPLACE INTO worlds (world_id, name_ja, name_en, setting, theme_color) VALUES ('medieval', '中世', 'Medieval', '石造りの村と封印された地下聖堂', NULL);
INSERT OR REPLACE INTO worlds (world_id, name_ja, name_en, setting, theme_color) VALUES ('future', '未来', 'Future', '錆びたセクターと機械の祈り', NULL);
INSERT OR REPLACE INTO events (event_id, world_id, type, text_ja, text_en, speaker, choices_json, reaction_slots_json, conditions_json, effects_json) VALUES ('m_e01', 'medieval', 'explore', '村の入口に立つ。石畳は苔むし、足跡ひとつない。門の向こうで何かが待っている気配がする。', NULL, NULL, '[{"label":"門を潜る","score":1,"tags":["bold","reckless"],"effect":{"type":"damage","value":5},"result_text":"門の仕掛けが作動し、石片が降り注ぐ。","result_text_en":"A trap triggers—stone fragments rain down."},{"label":"周囲を調べる","score":2,"tags":["cautious","thorough"],"discovery":true,"effect":{"type":"gold","value":15},"result_text":"苔の下に古い金貨を見つけた。","result_text_en":"You find old coins beneath the moss."}]', '[{"conditions":{"min_loop":3},"text":"\n\n……この道を歩いた記憶がある。何度も。"},{"conditions":{"requires_flag":"mayor_basement_seen"},"text":"\n\n石畳の下、あの地下室に繋がる道があるはずだ。"},{"conditions":{"requires_foreign_job":true,"requires_job":"cyborg"},"text":"\n\n機械の身体が違和感を訴える。この世界には、お前の部品の原料がない。"}]', NULL, NULL);
INSERT OR REPLACE INTO events (event_id, world_id, type, text_ja, text_en, speaker, choices_json, reaction_slots_json, conditions_json, effects_json) VALUES ('m_e02', 'medieval', 'dialogue', '村長の家。扉を叩くと、老人が隙間から覗く。「あんた、何度目だ？」意味のわからない問いかけ。地下から軋む音がする。', NULL, 'elder', '[{"label":"「何度目とは？」と問う","score":2,"tags":["curious","defiant"],"result_text":"老人は答えない。ただ目を逸らす。","result_text_en":"The old man looks away without answering."},{"label":"地下について尋ねる","score":3,"tags":["curious","reckless"],"discovery":true,"sets_flag":"mayor_basement_asked","effect":{"type":"damage","value":8},"result_text":"地下から冷気が這い上がり、体温を奪う。","result_text_en":"Cold air crawls up from below, draining your warmth."},{"label":"立ち去る","score":1,"tags":["cautious"],"effect":{"type":"heal","value":5},"result_text":"距離を取り、息を整える。","result_text_en":"You step back and catch your breath."},{"label":"「あんたは誰だ？」と聞き返す","score":2,"tags":["curious","empathetic"],"sets_flag":"aldus_name_asked","result_text":"老人の表情が僅かに和らぐ。","result_text_en":"The old man''s expression softens slightly."}]', '[{"conditions":{"requires_tag":"curious","tag_threshold":3},"text":"\n\n老人の目が揺らぐ。「お前は……覚えている方か」"},{"conditions":{"requires_truth_stage":2},"text":"\n\n老人の顔に見覚えがある。何度も見た。何度も死んだ。"},{"conditions":{"requires_foreign_job":true,"requires_job":"cyborg"},"text":"\n\n老人が後ずさる。「なんだその身体は……悪魔の業か」"}]', NULL, NULL);
INSERT OR REPLACE INTO events (event_id, world_id, type, text_ja, text_en, speaker, choices_json, reaction_slots_json, conditions_json, effects_json) VALUES ('m_e02b', 'medieval', 'dialogue', '村長は沈黙する。しかし目は地下への扉を見ている。「……見たいのか。見れば、お前も残る」', NULL, 'elder', '[{"label":"地下へ降りる","score":5,"tags":["bold","reckless","curious"],"discovery":true},{"label":"やめておく","score":1,"tags":["cautious","obedient"]}]', '[{"conditions":{"requires_tag":"defiant","tag_threshold":5},"text":"\n\n「残る、か。俺は何度でも戻ってくる」"}]', '{"requires_flag":"mayor_basement_asked"}', NULL);
INSERT OR REPLACE INTO events (event_id, world_id, type, text_ja, text_en, speaker, choices_json, reaction_slots_json, conditions_json, effects_json) VALUES ('m_e02c', 'medieval', 'dialogue', '老人——アルダスは暖炉の前に座る。「娘がいた。エリスという。井戸に身を投げた……いや、投げたのではない。呼ばれたんだ」', NULL, 'elder', '[{"label":"「呼ばれた？誰に？」","score":3,"tags":["curious","empathetic"],"discovery":true,"sets_flag":"eris_caller_asked"},{"label":"「……すまない」","score":2,"tags":["merciful","empathetic"]},{"label":"黙って聞く","score":1,"tags":["cautious","obedient"]}]', '[{"conditions":{"min_loop":5},"text":"\n\nアルダスの目に涙はない。枯れたのだろう。何度も同じ話をして。"},{"conditions":{"requires_flag":"n06_mourned"},"text":"\n\nN-06の顔が、ふと頭をよぎる。誰かを弔う行為に、世界の境目はないのか。"}]', '{"requires_flag":"aldus_name_asked"}', NULL);
INSERT OR REPLACE INTO events (event_id, world_id, type, text_ja, text_en, speaker, choices_json, reaction_slots_json, conditions_json, effects_json) VALUES ('m_e02d', 'medieval', 'dialogue', 'アルダスが震える手で扉を閉める。「わしはな……お前が初めてじゃない。いや、わし自身が……何度目かわからん」
彼の手の甲に、培養槽の識別番号が刻まれている。「N-01」。', NULL, 'elder', '[{"label":"「あんたもResidueか」","score":5,"tags":["defiant","empathetic"],"discovery":true},{"label":"「N-01……」","score":4,"tags":["curious","thorough"],"discovery":true,"conditions":{"requires_flag":"tank_examined"}},{"label":"手を握る","score":3,"tags":["merciful","empathetic"]}]', '[{"conditions":{"requires_flag":"tank_examined"},"text":"\n\n培養槽で見た識別番号。N-01。最初の被験体は——この老人だった。"}]', '{"min_loop":7,"requires_flag":"eris_story_heard","requires_truth_stage":2}', NULL);
INSERT OR REPLACE INTO events (event_id, world_id, type, text_ja, text_en, speaker, choices_json, reaction_slots_json, conditions_json, effects_json) VALUES ('m_e03', 'medieval', 'explore', '崩れた礼拝堂。祭壇には古い血痕。壁の聖画は削り取られ、代わりに何かの紋章が刻まれている。', NULL, NULL, '[{"label":"紋章を調べる","score":2,"tags":["curious","thorough"],"discovery":true,"sets_flag":"emblem_examined","effect":{"type":"gold","value":10},"result_text":"紋章の裏から宝飾品が落ちた。","result_text_en":"A jeweled trinket falls from behind the emblem."},{"label":"祭壇に祈る","score":1,"tags":["merciful","obedient"],"effect":{"type":"heal","value":15},"result_text":"温かい光が傷を癒す。","result_text_en":"Warm light soothes your wounds."},{"label":"先へ進む","score":1,"tags":["hasty"],"result_text":"振り返らずに歩き出す。","result_text_en":"You move on without looking back."}]', '[{"conditions":{"requires_flag":"bishop_defeated"},"text":"\n\n紋章が光る。司教を倒した記憶が、この場所に染み付いている。"}]', NULL, NULL);
INSERT OR REPLACE INTO events (event_id, world_id, type, text_ja, text_en, speaker, choices_json, reaction_slots_json, conditions_json, effects_json) VALUES ('m_e03b', 'medieval', 'explore', '紋章をよく見る。二重の円環と、中心の欠け。どこかで見た——未来の施設で。廃棄セクターの隔壁に刻まれていた紋章と、同じだ。
中世の礼拝堂に、未来の印がある。', NULL, NULL, '[{"label":"紋章を記録する","score":3,"tags":["curious","thorough"],"discovery":true},{"label":"紋章を削る","score":2,"tags":["defiant","reckless"],"sets_flag":"emblem_destroyed"}]', '[{"conditions":{"requires_foreign_job":true,"requires_job":"cyborg"},"text":"\n\n機械の目が紋章をスキャンする。「プロジェクト・Residue 認証マーク。登録日：不明。エラー：時系列矛盾」"}]', '{"requires_flag":"emblem_examined","requires_truth_stage":1}', NULL);
INSERT OR REPLACE INTO events (event_id, world_id, type, text_ja, text_en, speaker, choices_json, reaction_slots_json, conditions_json, effects_json) VALUES ('m_e04', 'medieval', 'battle', '廃屋の影から、かつて人だったものが現れる。錆びた剣を引きずり、虚ろな眼窩がお前を捉える。', NULL, NULL, '[{"label":"戦う","score":0,"start_battle":"m_hollow_guard","tags":["bold"]},{"label":"逃げる（前のノードへ戻る）","score":0,"flee":true,"tags":["cautious"]},{"label":"話しかける","score":3,"tags":["merciful","empathetic"],"conditions":{"requires_tag":"merciful","tag_threshold":3},"sets_flag":"hollow_guard_spoken","discovery":true}]', '[{"conditions":{"requires_tag":"merciful","tag_threshold":5},"text":"\n\nその姿に、かつての人間を見る。救いはあるのか。"},{"conditions":{"requires_foreign_job":true,"requires_job":"cyborg"},"text":"\n\n虚ろな目がお前の機械部品を見つめる。かつて人だったもの同士、通じ合うものがあるのか。"},{"conditions":{"requires_flag":"hollow_guard_spoken"},"text":"\n\n衛兵の眼窩に一瞬、光が戻る。「……地下に……あの子が……」そして再び虚ろに還った。"}]', NULL, NULL);
INSERT OR REPLACE INTO events (event_id, world_id, type, text_ja, text_en, speaker, choices_json, reaction_slots_json, conditions_json, effects_json) VALUES ('m_e05', 'medieval', 'dialogue', '井戸の底から声が聞こえる。「お前もここへ来たか」——誰の声かわからない。いや、自分の声に似ている。', NULL, 'unknown', '[{"label":"井戸を覗き込む","score":3,"tags":["reckless","curious","fearless"],"discovery":true,"sets_flag":"well_peeked","effect":{"type":"damage","value":12},"result_text":"覗き込んだ瞬間、何かに引き寄せられ頭を打つ。","result_text_en":"Something pulls you—you hit your head."},{"label":"応えない","score":1,"tags":["cautious","pragmatic"],"effect":{"type":"heal","value":5},"result_text":"声は消え、静けさが戻る。","result_text_en":"The voice fades. Silence returns."}]', '[{"conditions":{"min_loop":7},"text":"\n\n何度も聞いた声だ。何度も死んだ自分の声。"},{"conditions":{"requires_truth_stage":1},"text":"\n\n声は続ける。「毎回、同じことを聞く。毎回、同じ答えを返す」"}]', NULL, NULL);
INSERT OR REPLACE INTO events (event_id, world_id, type, text_ja, text_en, speaker, choices_json, reaction_slots_json, conditions_json, effects_json) VALUES ('m_e05b', 'medieval', 'explore', '井戸の底に降りる。水は干上がり、代わりに——骨がある。お前と同じ装備の。同じ傷の。
声が囁く。「やっと降りてきたか。何度覗いても降りなかったのに」
それはお前だ。前の周回の、お前の残骸。', NULL, 'self', '[{"label":"骨を拾い上げる","score":3,"tags":["bold","empathetic"],"discovery":true,"sets_flag":"past_self_bone"},{"label":"「お前は俺じゃない」","score":2,"tags":["defiant"]},{"label":"急いで這い上がる","score":1,"tags":["cautious"],"flee":true}]', '[{"conditions":{"requires_flag":"residue_truth_revealed"},"text":"\n\n「Residue——お前も俺も、同じものだ。消し残り同士、仲良くしようぜ」"}]', '{"requires_flag":"well_peeked","min_loop":5}', NULL);
INSERT OR REPLACE INTO events (event_id, world_id, type, text_ja, text_en, speaker, choices_json, reaction_slots_json, conditions_json, effects_json) VALUES ('m_e06', 'medieval', 'explore', '地下墓地の入口。階段を降りると、湿った空気と腐敗臭。壁には無数の手形。誰かが這い上がろうとした痕跡。', NULL, NULL, '[{"label":"降りる","score":2,"tags":["bold","fearless"],"effect":{"type":"damage","value":10},"result_text":"階段で足を滑らせ、腕を擦りむく。","result_text_en":"You slip on the stairs and scrape your arm."},{"label":"手形を調べる","score":2,"tags":["thorough","curious"],"discovery":true,"effect":{"type":"gold","value":20},"result_text":"手形の奥に隠された小部屋を発見。宝が眠っていた。","result_text_en":"Behind the handprints, a hidden alcove with treasure."}]', '[{"conditions":{"requires_flag":"well_peeked"},"text":"\n\n井戸で聞いた声が、ここでも響く。「お前も這い上がれ」"}]', NULL, NULL);
INSERT OR REPLACE INTO events (event_id, world_id, type, text_ja, text_en, speaker, choices_json, reaction_slots_json, conditions_json, effects_json) VALUES ('m_e07', 'medieval', 'dialogue', '封印の間。床の紋章が淡く光る。声が響く——「封印を解くな」「解かねば真実は得られぬ」二つの声。どちらが正しい？', NULL, 'elder', '[{"label":"封印を解く","score":3,"tags":["defiant","reckless","curious"],"discovery":true,"sets_flag":"seal_broken","effect":{"type":"damage","value":15},"result_text":"封印が弾け、衝撃波が身体を貫く。","result_text_en":"The seal shatters—a shockwave pierces your body."},{"label":"封印を強化する","score":2,"tags":["obedient","cautious"],"sets_flag":"seal_reinforced","discovery":true,"effect":{"type":"heal","value":10},"result_text":"紋章が光り、安堵の波が広がる。","result_text_en":"The emblem glows. A wave of relief."},{"label":"何もしない","score":1,"tags":["pragmatic"],"result_text":"静かに立ち去る。","result_text_en":"You quietly leave."}]', '[{"conditions":{"requires_tag":"defiant","tag_threshold":5},"text":"\n\n「正しさなど関係ない。俺は知りたいだけだ」"},{"conditions":{"requires_truth_stage":2},"text":"\n\n二つの声は、同じ存在から発せられている。司教の——"}]', NULL, NULL);
INSERT OR REPLACE INTO events (event_id, world_id, type, text_ja, text_en, speaker, choices_json, reaction_slots_json, conditions_json, effects_json) VALUES ('m_e07b', 'medieval', 'dialogue', '封印が解けた空間に、二つの残響が絡み合っている。
「私は封じた」「私は観た」
「過去から」「未来から」
「同じ場所に、同じ罪を」
司教とコア預言者——時間の両端に立つ二人は、同じ紋章の下で繋がっていた。', NULL, 'unknown', '[{"label":"「同じ罪とは？」","score":3,"tags":["curious","defiant"],"discovery":true},{"label":"「お前たちはResidue計画の創始者か」","score":4,"tags":["curious","thorough"],"discovery":true,"conditions":{"requires_flag":"emblem_cross_recognized"}},{"label":"耳を塞ぐ","score":1,"tags":["cautious","obedient"]}]', NULL, '{"requires_flag":"seal_broken","min_loop":5,"requires_truth_stage":1}', NULL);
INSERT OR REPLACE INTO events (event_id, world_id, type, text_ja, text_en, speaker, choices_json, reaction_slots_json, conditions_json, effects_json) VALUES ('m_e08', 'medieval', 'explore', '祭壇の前室。蝋燭が勝手に灯る。祭壇には古い書物。最後のページには——お前の名前が書かれている。', NULL, NULL, '[{"label":"書物を読む","score":3,"tags":["curious","thorough"],"discovery":true,"sets_flag":"tome_read","effect":{"type":"gold","value":25},"result_text":"書物の間に金貨が挟まっていた。","result_text_en":"Gold coins were pressed between the pages."},{"label":"書物を燃やす","score":2,"tags":["defiant","reckless"],"sets_flag":"tome_burned","effect":{"type":"damage","value":10},"result_text":"炎が異様に燃え上がり、手を焼く。","result_text_en":"The flames flare unnaturally, burning your hands."},{"label":"そのまま進む","score":1,"tags":["hasty","pragmatic"]}]', '[{"conditions":{"requires_flag":"seal_broken"},"text":"\n\n封印を解いた瞬間から、この名前は変わっていない。何度繰り返しても。"},{"conditions":{"min_loop":10},"text":"\n\n数十回分の名前が重なっている。全て、お前の名前だ。"}]', NULL, NULL);
INSERT OR REPLACE INTO events (event_id, world_id, type, text_ja, text_en, speaker, choices_json, reaction_slots_json, conditions_json, effects_json) VALUES ('m_e08b', 'medieval', 'explore', '蝋燭の前に灰が積もっている。前回お前が燃やした書物の灰。だがその中に、燃え残った一片がある。
「——Residueは消えない。燃やしても。砕いても。忘れても——」
灰の中に文字が浮かぶ。火すら消せなかった言葉。', NULL, NULL, '[{"label":"灰を集める","score":3,"tags":["curious","thorough"],"discovery":true},{"label":"もう一度燃やす","score":2,"tags":["defiant","reckless"]}]', NULL, '{"requires_flag":"tome_burned"}', NULL);
INSERT OR REPLACE INTO events (event_id, world_id, type, text_ja, text_en, speaker, choices_json, reaction_slots_json, conditions_json, effects_json) VALUES ('m_e_boss', 'medieval', 'battle', '地下聖堂の最奥。玉座に座る者の口は糸で縫い合わされている。しかし声が頭に響く。「お前は何度も来た。何度も死んだ。そして何度も忘れた。今度こそ覚えていられるか？」', NULL, NULL, '[{"label":"「覚えている」と答える","score":0,"start_battle":"m_boss_sealed_bishop","tags":["defiant","fearless"]},{"label":"「何の話だ」と答える","score":0,"start_battle":"m_boss_sealed_bishop","tags":["pragmatic"]},{"label":"逃げる","score":0,"flee":true,"tags":["cautious"]}]', '[{"conditions":{"requires_flag":"tome_read"},"text":"\n\n「書物を読んだか。ならば知っているはずだ——お前が何者か」"},{"conditions":{"requires_truth_stage":2},"text":"\n\n「もう隠す必要はない。お前は Residue だ。消し残り。死の痕跡」"},{"conditions":{"min_loop":7,"requires_flag":"mayor_basement_seen"},"text":"\n\n「お前は消したと思っている。この世界は忘れない。残るんだ。Residue として」"},{"conditions":{"requires_foreign_job":true,"requires_job":"cyborg"},"text":"\n\n「異質な肉体を纏って来たか……未来の残滓が、過去の残滓に会いに来るとは」"}]', NULL, NULL);
INSERT OR REPLACE INTO events (event_id, world_id, type, text_ja, text_en, speaker, choices_json, reaction_slots_json, conditions_json, effects_json) VALUES ('m_e_residue_reveal', 'medieval', 'dialogue', '「Residue——それがお前の正体だ。死んでも消えない。繰り返す。残り続ける。この世界のすべてが、お前の痕跡で染まっている」', NULL, 'bishop', '[{"label":"「だから何度でも戻ってくる」","score":5,"tags":["defiant","fearless"],"discovery":true},{"label":"「忘れさせてくれ」","score":3,"tags":["merciful","empathetic"]}]', NULL, '{"min_loop":7,"requires_truth_stage":2,"requires_flag":"mayor_basement_seen"}', NULL);
INSERT OR REPLACE INTO events (event_id, world_id, type, text_ja, text_en, speaker, choices_json, reaction_slots_json, conditions_json, effects_json) VALUES ('m_cross_cipher_delivery', 'medieval', 'dialogue', '修道士の霊が現れる。石化した身体から抜け出した、透明な輪郭。「……それは、我々が失った暗号だ。未来から持ち帰ったのか？」', NULL, 'scholar', '[{"label":"暗号を渡す","score":5,"tags":["merciful","curious"],"discovery":true,"sets_flag":"cross_cipher_delivered"},{"label":"渡さない","score":1,"tags":["greedy","cautious"]}]', '[{"conditions":{"requires_truth_stage":2},"text":"\n\n「お前は何度もこの暗号を見ている。だが、初めて持ち帰った」"}]', '{"requires_cross_link_item":"ancient_cipher"}', NULL);
INSERT OR REPLACE INTO events (event_id, world_id, type, text_ja, text_en, speaker, choices_json, reaction_slots_json, conditions_json, effects_json) VALUES ('m_cross_cipher_revelation', 'medieval', 'dialogue', '暗号を解読した霊が語る。「これは……司教が未来へ送った警告だ。『Residueを止めろ』。だが未来は聞かなかった。お前を作り続けた」', NULL, 'scholar', '[{"label":"「俺を止める方法は？」","score":3,"tags":["curious","defiant"],"discovery":true},{"label":"「止める必要があるのか」","score":3,"tags":["pragmatic","empathetic"]}]', NULL, '{"requires_flag":"cross_cipher_delivered"}', NULL);
INSERT OR REPLACE INTO events (event_id, world_id, type, text_ja, text_en, speaker, choices_json, reaction_slots_json, conditions_json, effects_json) VALUES ('m_foreign_cyborg_01', 'medieval', 'dialogue', '村人たちが遠巻きにお前を見ている。「あれは……人か？」「悪魔の作り物だ」機械の身体は、この時代には異物でしかない。', NULL, 'elder', '[{"label":"気にしない","score":1,"tags":["cold","pragmatic"]},{"label":"「俺も人間だ」と言う","score":2,"tags":["empathetic","defiant"]},{"label":"威嚇する","score":1,"tags":["cruel","bold"]}]', NULL, '{"requires_foreign_job":true,"requires_job":"cyborg"}', NULL);
INSERT OR REPLACE INTO events (event_id, world_id, type, text_ja, text_en, speaker, choices_json, reaction_slots_json, conditions_json, effects_json) VALUES ('m_foreign_cyborg_02', 'medieval', 'dialogue', '礼拝堂の残響がお前の機械部品に干渉する。エラーログが点滅する。「不明なエネルギー場を検出。分析不能」——この世界の呪いは、未来の技術でも解析できない。', NULL, 'cyborg', '[{"label":"データを記録する","score":2,"tags":["thorough","curious"],"discovery":true},{"label":"システムを再起動する","score":1,"tags":["pragmatic"]},{"label":"無視して進む","score":1,"tags":["reckless"]}]', NULL, '{"requires_foreign_job":true,"requires_job":"cyborg"}', NULL);
INSERT OR REPLACE INTO events (event_id, world_id, type, text_ja, text_en, speaker, choices_json, reaction_slots_json, conditions_json, effects_json) VALUES ('f_e01', 'future', 'explore', '廃棄セクターの入口。錆びた隔壁には「立入禁止：記録消去対象区画」と書かれている。消されたはずの場所。', NULL, NULL, '[{"label":"侵入する","score":1,"tags":["bold","reckless"],"effect":{"type":"damage","value":8},"result_text":"セキュリティレーザーが腕を掠める。","result_text_en":"A security laser grazes your arm."},{"label":"周囲のログを確認する","score":2,"tags":["cautious","thorough"],"discovery":true,"effect":{"type":"gold","value":15},"result_text":"ログの中に資源データの座標を発見。","result_text_en":"You find resource coordinates in the logs."}]', '[{"conditions":{"min_loop":3},"text":"\n\nこの警告を、何度見ただろう。"},{"conditions":{"requires_flag":"core_log_read"},"text":"\n\n消去ログに、お前の侵入記録がある。47回。"},{"conditions":{"requires_foreign_job":true,"requires_job":"knight"},"text":"\n\n鎧の重さが、金属の床に響く。この世界には、お前のような存在は記録されていない。"}]', NULL, NULL);
INSERT OR REPLACE INTO events (event_id, world_id, type, text_ja, text_en, speaker, choices_json, reaction_slots_json, conditions_json, effects_json) VALUES ('f_e02', 'future', 'dialogue', '認証ターミナルが起動する。「ようこそ、被験体N-07」——覚えのない識別名。「前回の終了記録：死亡」と表示される。', NULL, 'cyborg', '[{"label":"認証を通す","score":2,"tags":["obedient","pragmatic"],"result_text":"システムがお前を受け入れる。","result_text_en":"The system accepts you."},{"label":"端末をハックする","score":3,"tags":["defiant","curious"],"discovery":true,"sets_flag":"terminal_hacked","effect":{"type":"gold","value":20},"result_text":"ハック成功。機密データと共にクレジットを獲得。","result_text_en":"Hack successful. Credits and classified data obtained."},{"label":"無視して進む","score":1,"tags":["hasty"],"effect":{"type":"damage","value":5},"result_text":"セキュリティが反応し、電撃を受ける。","result_text_en":"Security responds with an electric shock."}]', '[{"conditions":{"requires_tag":"curious","tag_threshold":5},"text":"\n\n「死亡回数：46」追加情報が表示される。"},{"conditions":{"requires_truth_stage":1},"text":"\n\n「残存記憶：断片的。人格継続性：未確認」システムが診断を続ける。"},{"conditions":{"requires_foreign_job":true,"requires_job":"knight"},"text":"\n\nターミナルがエラーを吐く。「生体認証失敗。不明な有機体。中世期の金属装備を検出」"}]', NULL, NULL);
INSERT OR REPLACE INTO events (event_id, world_id, type, text_ja, text_en, speaker, choices_json, reaction_slots_json, conditions_json, effects_json) VALUES ('f_e03', 'future', 'explore', '整備区画。放棄された作業台に、誰かの日記データが残っている。「もう17回目だ。記憶は消されても、体は覚えている」', NULL, NULL, '[{"label":"日記を全て読む","score":3,"tags":["curious","thorough"],"discovery":true,"sets_flag":"diary_read","result_text":"N-06の記録が脳裏に焼き付く。","result_text_en":"N-06''s records burn into your mind."},{"label":"データを消去する","score":1,"tags":["cautious","pragmatic"],"effect":{"type":"damage","value":5},"result_text":"データ消去の反動で端末が爆発。","result_text_en":"The terminal explodes from the data purge."},{"label":"工具を回収する","score":2,"tags":["greedy","pragmatic"],"effect":{"type":"gold","value":25},"result_text":"工具の中に希少な部品を発見。","result_text_en":"You find rare parts among the tools."}]', '[{"conditions":{"requires_flag":"terminal_hacked"},"text":"\n\n日記の筆者ID：N-06。お前の一つ前のモデル。"},{"conditions":{"min_loop":5},"text":"\n\n「18回目の俺へ。逃げるな。真実を見ろ」追記があった。"}]', NULL, NULL);
INSERT OR REPLACE INTO events (event_id, world_id, type, text_ja, text_en, speaker, choices_json, reaction_slots_json, conditions_json, effects_json) VALUES ('f_e03b', 'future', 'explore', '日記の最後に暗号化された記録がある。ハッキングスキルで解読すると——

「俺の名前はカイだ。N-06という番号じゃない。カイだ。
預言者を倒す方法を見つけた。だが代償は——次のモデルを起動すること。
つまり、俺が死ぬこと。
俺は選ばなかった。だからお前が生まれた。N-07。
 お前が読んでいるなら、俺の臆病もResidueとして残ったということだ」', NULL, NULL, '[{"label":"「カイ……お前は臆病じゃない」","score":3,"tags":["empathetic","merciful"],"discovery":true},{"label":"倒す方法を記録する","score":3,"tags":["pragmatic","thorough"],"discovery":true,"sets_flag":"prophet_weakness_known"},{"label":"データを閉じる","score":1,"tags":["cautious"]}]', NULL, '{"requires_flag":"diary_read","requires_flag_2":"terminal_hacked"}', NULL);
INSERT OR REPLACE INTO events (event_id, world_id, type, text_ja, text_en, speaker, choices_json, reaction_slots_json, conditions_json, effects_json) VALUES ('f_e04', 'future', 'dialogue', 'データ保管庫。削除されたログが次々と復元される。全てお前の記録——何十回もの侵入、何十回もの死。同じ場所で、同じように。', NULL, 'cyborg', '[{"label":"記録を精査する","score":3,"tags":["thorough","curious"],"discovery":true},{"label":"記録を完全消去する","score":2,"tags":["defiant","reckless"]},{"label":"目を背ける","score":1,"tags":["cautious"]}]', '[{"conditions":{"requires_flag":"diary_read"},"text":"\n\nN-06の記録も混じっている。N-05も。N-01まで遡る。全員、同じ顔だ。"},{"conditions":{"requires_truth_stage":2},"text":"\n\n「プロジェクト・Residue：被験体の記憶を消去しても、行動パターンは残る」"}]', NULL, NULL);
INSERT OR REPLACE INTO events (event_id, world_id, type, text_ja, text_en, speaker, choices_json, reaction_slots_json, conditions_json, effects_json) VALUES ('f_e05', 'future', 'explore', '研究棟。培養槽には何も——いや、一つだけ、お前と同じ顔をした死体が浮いている。「Model N-06」とラベルが貼られている。', NULL, NULL, '[{"label":"培養槽を調べる","score":3,"tags":["curious","thorough"],"discovery":true,"sets_flag":"tank_examined","result_text":"培養槽の制御パネルにアクセスした。","result_text_en":"You access the tank''s control panel."},{"label":"培養槽を破壊する","score":2,"tags":["defiant","reckless","cruel"],"effect":{"type":"damage","value":12},"result_text":"ガラスの破片が手に刺さる。","result_text_en":"Glass shards cut into your hands."},{"label":"見なかったことにする","score":1,"tags":["cautious","pragmatic"]},{"label":"N-06に黙祷する","score":2,"tags":["merciful","empathetic"],"sets_flag":"n06_mourned","discovery":true,"effect":{"type":"heal","value":10},"result_text":"弔いの静けさが、心を落ち着かせる。","result_text_en":"The stillness of mourning calms your heart."}]', '[{"conditions":{"requires_flag":"core_log_read"},"text":"\n\nお前はN-07。つまり、あと何体いるのか。"},{"conditions":{"min_loop":7},"text":"\n\n培養槽の奥に、さらに多くの槽が見える。N-08、N-09……空だ。まだ。"}]', NULL, NULL);
INSERT OR REPLACE INTO events (event_id, world_id, type, text_ja, text_en, speaker, choices_json, reaction_slots_json, conditions_json, effects_json) VALUES ('f_e06', 'future', 'dialogue', '緊急シェルターの壁に刻まれたメッセージ。「逃げろ。コアに近づくな。お前が何者か、知らないほうがいい」——筆跡はお前のものに似ている。', NULL, 'unknown', '[{"label":"コアを目指す","score":2,"tags":["bold","defiant","fearless"],"effect":{"type":"damage","value":10},"result_text":"コアへの道は険しく、身体を削る。","result_text_en":"The path to the core is harsh on your body."},{"label":"メッセージを解読する","score":3,"tags":["thorough","curious"],"discovery":true,"sets_flag":"shelter_message_read","effect":{"type":"gold","value":15},"result_text":"メッセージの暗号に隠された座標を解読。","result_text_en":"You decode coordinates hidden in the message."},{"label":"引き返す","score":1,"flee":true,"tags":["cautious","obedient"]}]', '[{"conditions":{"requires_flag":"tank_examined"},"text":"\n\n筆跡を分析する。N-06のものだ。彼も、ここまで来た。"},{"conditions":{"requires_tag":"defiant","tag_threshold":5},"text":"\n\n「知らないほうがいい」——そんな選択肢は、とうに捨てた。"}]', NULL, NULL);
INSERT OR REPLACE INTO events (event_id, world_id, type, text_ja, text_en, speaker, choices_json, reaction_slots_json, conditions_json, effects_json) VALUES ('f_e07', 'future', 'explore', 'コアへの最終通路。警告が点滅する。「この先、人格保持は保証されません」システムがお前を止めようとしている。', NULL, NULL, '[{"label":"構わず進む","score":2,"tags":["reckless","fearless","defiant"],"effect":{"type":"damage","value":15},"result_text":"警告を無視した代償。身体が軋む。","result_text_en":"The cost of ignoring warnings. Your body creaks."},{"label":"システムと対話する","score":3,"tags":["curious","empathetic"],"discovery":true,"sets_flag":"system_dialogue","effect":{"type":"heal","value":10},"result_text":"システムが修復プロトコルを起動してくれた。","result_text_en":"The system activates a repair protocol for you."}]', '[{"conditions":{"requires_flag":"shelter_message_read"},"text":"\n\nN-06のメッセージが蘇る。彼は警告に従った。だから彼は、N-07に道を譲った。"},{"conditions":{"requires_truth_stage":2},"text":"\n\n「人格保持」——そもそも、お前に保持すべき人格があるのか？"}]', NULL, NULL);
INSERT OR REPLACE INTO events (event_id, world_id, type, text_ja, text_en, speaker, choices_json, reaction_slots_json, conditions_json, effects_json) VALUES ('f_e_boss_kai_assist', 'future', 'battle', 'コア預言者が笑う。「また来たか、N-07」
だが今回は違う。カイが遺した方法——預言者の観測パターンを逆手に取る。
「お前……それはN-06の……」預言者の声に、初めて動揺が混じる。', NULL, NULL, '[{"label":"「カイが見つけた方法だ」","score":0,"start_battle":"f_boss_core_prophet","tags":["defiant","fearless"],"battle_modifier":{"enemy_attack_reduction":5,"description":"カイの知識により預言者の攻撃パターンを予測"}},{"label":"「N-06の臆病が、お前を倒す」","score":0,"start_battle":"f_boss_core_prophet","tags":["empathetic","defiant"],"battle_modifier":{"enemy_attack_reduction":5,"description":"カイの知識により預言者の攻撃パターンを予測"}}]', '[{"conditions":{"requires_flag":"n06_mourned"},"text":"\n\n「カイの意志を継ぐ」——弔った記憶が、戦う理由になる。"}]', '{"requires_flag":"prophet_weakness_known"}', NULL);
INSERT OR REPLACE INTO events (event_id, world_id, type, text_ja, text_en, speaker, choices_json, reaction_slots_json, conditions_json, effects_json) VALUES ('f_e_boss', 'future', 'battle', '工場中枢。無数のケーブルに繋がれた意識体が待っている。「また会えたな、N-07。これで47回目だ。お前は毎回ここに来て、毎回負ける。そして毎回忘れる。今回は違うと、毎回言う。」', NULL, NULL, '[{"label":"「今回こそ違う」","score":0,"start_battle":"f_boss_core_prophet","tags":["defiant","fearless"]},{"label":"「なぜ教える？」","score":0,"start_battle":"f_boss_core_prophet","tags":["curious","empathetic"]},{"label":"逃げる","score":0,"flee":true,"tags":["cautious"]}]', '[{"conditions":{"requires_flag":"system_dialogue"},"text":"\n\n「システムと話したか。彼は私の一部だ。お前を導くために残した」"},{"conditions":{"requires_truth_stage":2},"text":"\n\n「Residue——お前たちはそう呼ばれている。消し残り。私の実験の残滓」"},{"conditions":{"min_loop":7,"requires_flag":"core_log_read"},"text":"\n\n「お前は消したと思っている。記録を。記憶を。だが、行動は消えない。残るんだ。Residue として」"},{"conditions":{"requires_foreign_job":true,"requires_job":"knight"},"text":"\n\n「過去の鎧を纏った残滓か……中世の因子が、未来の実験に干渉するとは」"}]', NULL, NULL);
INSERT OR REPLACE INTO events (event_id, world_id, type, text_ja, text_en, speaker, choices_json, reaction_slots_json, conditions_json, effects_json) VALUES ('f_e_residue_reveal', 'future', 'dialogue', '「Residue——消し残り。記憶を消しても、行動パターンは残る。お前は47回、同じ選択をしてきた。48回目も、同じだろう」', NULL, 'cyborg', '[{"label":"「なら48回目こそ変える」","score":5,"tags":["defiant","fearless"],"discovery":true},{"label":"「同じでいい。それが俺だ」","score":3,"tags":["pragmatic","empathetic"]}]', NULL, '{"min_loop":7,"requires_truth_stage":2,"requires_flag":"core_log_read"}', NULL);
INSERT OR REPLACE INTO events (event_id, world_id, type, text_ja, text_en, speaker, choices_json, reaction_slots_json, conditions_json, effects_json) VALUES ('f_cross_quantum_delivery', 'future', 'dialogue', 'データ保管庫の奥に、古い端末が眠っている。スクリーンに文字が浮かぶ。「量子回路を検出。中世期の封印技術との互換性：あり」——これを渡す相手がいる。', NULL, 'cyborg', '[{"label":"回路を端末に接続する","score":5,"tags":["curious","bold"],"discovery":true,"sets_flag":"cross_quantum_delivered"},{"label":"接続しない","score":1,"tags":["cautious"]}]', '[{"conditions":{"requires_truth_stage":2},"text":"\n\n端末が追加情報を表示する。「この回路は、司教が作った。未来を予見して」"}]', '{"requires_cross_link_item":"quantum_circuit"}', NULL);
INSERT OR REPLACE INTO events (event_id, world_id, type, text_ja, text_en, speaker, choices_json, reaction_slots_json, conditions_json, effects_json) VALUES ('f_cross_quantum_revelation', 'future', 'dialogue', '回路が起動する。中世の司教の記録が再生される。「この回路を見つけたお前へ。私は未来を見た。Residueプロジェクトを。お前たちを。止める方法は、ある」', NULL, 'scholar', '[{"label":"「止める方法とは？」","score":3,"tags":["curious","defiant"],"discovery":true},{"label":"「なぜ止める必要がある」","score":3,"tags":["pragmatic","empathetic"]}]', NULL, '{"requires_flag":"cross_quantum_delivered"}', NULL);
INSERT OR REPLACE INTO events (event_id, world_id, type, text_ja, text_en, speaker, choices_json, reaction_slots_json, conditions_json, effects_json) VALUES ('f_foreign_knight_01', 'future', 'dialogue', 'セキュリティスキャナがお前を検出する。「警告：未登録の金属装甲を検出。時代区分：中世。汚染リスク：不明」監視ドローンが集まってくる。', NULL, 'cyborg', '[{"label":"ドローンを破壊する","score":1,"tags":["bold","reckless"]},{"label":"鎧を脱ぐ","score":2,"tags":["pragmatic","cautious"]},{"label":"無視して進む","score":1,"tags":["defiant"]}]', NULL, '{"requires_foreign_job":true,"requires_job":"knight"}', NULL);
INSERT OR REPLACE INTO events (event_id, world_id, type, text_ja, text_en, speaker, choices_json, reaction_slots_json, conditions_json, effects_json) VALUES ('f_foreign_knight_02', 'future', 'dialogue', '培養槽の前で、システムが語りかける。「騎士の誓い——データベースに記録がある。中世の概念。『守るべきもののために死ぬ』。お前は、何を守っている？」', NULL, 'cyborg', '[{"label":"「真実を」","score":2,"tags":["defiant","curious"]},{"label":"「自分自身を」","score":2,"tags":["pragmatic"]},{"label":"「もう何も」","score":1,"tags":["nihilistic","empathetic"]}]', NULL, '{"requires_foreign_job":true,"requires_job":"knight"}', NULL);
INSERT OR REPLACE INTO events (event_id, world_id, type, text_ja, text_en, speaker, choices_json, reaction_slots_json, conditions_json, effects_json) VALUES ('f_foreign_knight_03', 'future', 'explore', 'コアへの通路で、お前の鎧が反応する。金属が共鳴し、微かな声が聞こえる。「この場所を……覚えている」——鎧に宿った記憶が、未来の施設を認識している。', NULL, NULL, '[{"label":"記憶を辿る","score":3,"tags":["curious","thorough"],"discovery":true},{"label":"記憶を無視する","score":1,"tags":["pragmatic"]},{"label":"鎧を脱ぎ捨てる","score":2,"tags":["defiant","reckless"]}]', NULL, '{"requires_foreign_job":true,"requires_job":"knight"}', NULL);
INSERT OR REPLACE INTO nodes (node_id, world_id, name_ja, name_en, node_type, description_ja, description_en, edges_json, event_ids_json, enemy_ids_json) VALUES ('m_n01', 'medieval', '村の入口', NULL, 'explore', '石畳の道が霧の中へ続く。村人の姿は見えない。', NULL, '{"forward":"m_n02","left":null,"right":null,"back":null}', '["m_e01","m_foreign_cyborg_01"]', '["m_crawling_shade"]');
INSERT OR REPLACE INTO nodes (node_id, world_id, name_ja, name_en, node_type, description_ja, description_en, edges_json, event_ids_json, enemy_ids_json) VALUES ('m_n02', 'medieval', '村長の家', NULL, 'event', '窓は塞がれ、地下への扉が軋む音がする。', NULL, '{"forward":"m_n03","left":"m_n04","right":null,"back":"m_n01"}', '["m_e02","m_e02b"]', '[]');
INSERT OR REPLACE INTO nodes (node_id, world_id, name_ja, name_en, node_type, description_ja, description_en, edges_json, event_ids_json, enemy_ids_json) VALUES ('m_n03', 'medieval', '礼拝堂跡', NULL, 'explore', '崩れた祭壇。祈りの残響が空気を震わせる。', NULL, '{"forward":"m_n05","left":null,"right":"m_n06","back":"m_n02"}', '["m_e03","m_foreign_cyborg_02"]', '["m_hollow_guard"]');
INSERT OR REPLACE INTO nodes (node_id, world_id, name_ja, name_en, node_type, description_ja, description_en, edges_json, event_ids_json, enemy_ids_json) VALUES ('m_n04', 'medieval', '廃屋の路地', NULL, 'battle', '影が動く。逃げ場はない。', NULL, '{"forward":"m_n07","left":null,"right":null,"back":"m_n02"}', '["m_e04"]', '["m_hollow_guard"]');
INSERT OR REPLACE INTO nodes (node_id, world_id, name_ja, name_en, node_type, description_ja, description_en, edges_json, event_ids_json, enemy_ids_json) VALUES ('m_n05', 'medieval', '井戸の広場', NULL, 'event', '干上がった井戸。底から声が聞こえる気がする。', NULL, '{"forward":"m_n08","left":"m_n06","right":null,"back":"m_n03"}', '["m_e05"]', '["m_crawling_shade","m_hollow_guard"]');
INSERT OR REPLACE INTO nodes (node_id, world_id, name_ja, name_en, node_type, description_ja, description_en, edges_json, event_ids_json, enemy_ids_json) VALUES ('m_n06', 'medieval', '崩れた塔', NULL, 'battle', '瓦礫の隙間から這い出るものがいる。', NULL, '{"forward":null,"left":null,"right":null,"back":"m_n03"}', '[]', '["m_crawling_shade"]');
INSERT OR REPLACE INTO nodes (node_id, world_id, name_ja, name_en, node_type, description_ja, description_en, edges_json, event_ids_json, enemy_ids_json) VALUES ('m_n07', 'medieval', '地下墓地入口', NULL, 'explore', '石の階段が闇へ降りる。湿った空気が肺を満たす。', NULL, '{"forward":"m_n09","left":null,"right":null,"back":"m_n04"}', '["m_e06"]', '["m_bone_collector","m_cursed_monk"]');
INSERT OR REPLACE INTO nodes (node_id, world_id, name_ja, name_en, node_type, description_ja, description_en, edges_json, event_ids_json, enemy_ids_json) VALUES ('m_n08', 'medieval', '朽ちた修道院', NULL, 'battle', '祈りの姿勢で固まった者たちがいる。一体だけ、動いた。', NULL, '{"forward":"m_n10","left":null,"right":null,"back":"m_n05"}', '["m_cross_cipher_delivery","m_cross_cipher_revelation"]', '["m_cursed_monk"]');
INSERT OR REPLACE INTO nodes (node_id, world_id, name_ja, name_en, node_type, description_ja, description_en, edges_json, event_ids_json, enemy_ids_json) VALUES ('m_n09', 'medieval', '骨の回廊', NULL, 'battle', '壁も床も骨で組まれている。その一部が歩き出す。', NULL, '{"forward":"m_n11","left":"m_n10","right":null,"back":"m_n07"}', '[]', '["m_bone_collector"]');
