INSERT OR REPLACE INTO enemies (enemy_id, name_ja, name_en, hp, attack, defense, description_ja, description_en, rewards_json) VALUES ('m_hollow_guard', '虚ろな衛兵', 'Hollow Guard', 30, 8, 3, 'かつて村を守っていた男。今は空洞の眼窩だけが残る。', 'A man who once protected the village. Now only hollow eye sockets remain.', '{"gold":5,"tags":[]}');
INSERT OR REPLACE INTO enemies (enemy_id, name_ja, name_en, hp, attack, defense, description_ja, description_en, rewards_json) VALUES ('m_crawling_shade', '這い寄る影', 'Crawling Shade', 20, 12, 1, '壁から剥がれた何か。形を持たぬ飢え。', 'Something peeled from the wall. Formless hunger.', '{"gold":3,"tags":["shadow_slayer"]}');
INSERT OR REPLACE INTO enemies (enemy_id, name_ja, name_en, hp, attack, defense, description_ja, description_en, rewards_json) VALUES ('m_cursed_monk', '呪われた修道士', 'Cursed Monk', 45, 10, 5, '祈りの姿勢のまま石化した男。近づくと動き出す。', 'A man petrified in prayer. Moves when approached.', '{"gold":8,"tags":[]}');
INSERT OR REPLACE INTO enemies (enemy_id, name_ja, name_en, hp, attack, defense, description_ja, description_en, rewards_json) VALUES ('m_bone_collector', '骨収集者', 'Bone Collector', 55, 14, 4, '地下墓地を徘徊する、骨で編まれた人型。', 'A humanoid woven from bones, wandering the catacombs.', '{"gold":12,"tags":[]}');
INSERT OR REPLACE INTO enemies (enemy_id, name_ja, name_en, hp, attack, defense, description_ja, description_en, rewards_json) VALUES ('m_boss_sealed_bishop', '封印されし司教', 'Sealed Bishop', 120, 18, 8, '村人が決して語らぬ存在。地下聖堂の最奥で待つ。その口は縫い合わされ、眼だけが真実を知る。', 'A being the villagers never speak of. Waits in the deepest catacomb. Lips sewn shut, yet eyes know the truth.', '{"gold":50,"tags":["bishop_slayer","truth_seeker"]}');
INSERT OR REPLACE INTO enemies (enemy_id, name_ja, name_en, hp, attack, defense, description_ja, description_en, rewards_json) VALUES ('f_drone_swarm', '廃棄ドローン群', 'Scrap Drone Swarm', 25, 6, 2, '壊れた偵察機の残骸。群れで襲う本能だけが残る。', 'Debris of broken scouts. Only the swarm instinct remains.', '{"gold":4,"tags":[]}');
INSERT OR REPLACE INTO enemies (enemy_id, name_ja, name_en, hp, attack, defense, description_ja, description_en, rewards_json) VALUES ('f_memory_glitch', '記憶のグリッチ', 'Memory Glitch', 35, 15, 0, '削除されたはずのデータが実体化したもの。触れると過去が乱れる。', 'Deleted data that has materialized. Touch it, and the past becomes unstable.', '{"gold":6,"tags":["glitch_touched"]}');
INSERT OR REPLACE INTO enemies (enemy_id, name_ja, name_en, hp, attack, defense, description_ja, description_en, rewards_json) VALUES ('f_rusted_sentinel', '錆びた歩哨', 'Rusted Sentinel', 50, 12, 8, 'セクター警備の自動兵。動力は切れているはずだが、何かが動かしている。', 'Sector security automaton. Power should be off, but something drives it.', '{"gold":10,"tags":[]}');
INSERT OR REPLACE INTO enemies (enemy_id, name_ja, name_en, hp, attack, defense, description_ja, description_en, rewards_json) VALUES ('f_echo_hunter', '残響狩り', 'Echo Hunter', 60, 16, 5, '死者の電子痕跡を追う存在。お前の死を覚えている。', 'An entity tracking electronic traces of the dead. It remembers your death.', '{"gold":15,"tags":[]}');
INSERT OR REPLACE INTO enemies (enemy_id, name_ja, name_en, hp, attack, defense, description_ja, description_en, rewards_json) VALUES ('f_boss_core_prophet', 'コア預言者', 'Core Prophet', 140, 20, 10, '工場中枢に眠る意識体。全ての周回を観測し、記録し続けてきた。お前の死を何度も見たと告げる。', 'A consciousness sleeping in the factory core. Has observed and recorded every loop. It tells you it has seen your death many times.', '{"gold":60,"tags":["prophet_witness","machine_breaker"]}');
INSERT OR REPLACE INTO enemies (enemy_id, name_ja, name_en, hp, attack, defense, description_ja, description_en, rewards_json) VALUES ('m_restless_villager', '彷徨う村人', 'Restless Villager', 35, 9, 4, '死に方を忘れた村人。終わりなく彷徨う。', 'A villager who forgot how to die. Wanders endlessly.', '{"gold":7,"tags":[]}');
INSERT OR REPLACE INTO enemies (enemy_id, name_ja, name_en, hp, attack, defense, description_ja, description_en, rewards_json) VALUES ('m_altar_wraith', '祭壇の亡霊', 'Altar Wraith', 40, 11, 3, '崩れた祭壇に縛られた霊。生者を拒む。', 'A spirit bound to the ruined altar. Lashes out at the living.', '{"gold":9,"tags":[]}');
INSERT OR REPLACE INTO enemies (enemy_id, name_ja, name_en, hp, attack, defense, description_ja, description_en, rewards_json) VALUES ('f_corrupted_drone', '汚染ドローン', 'Corrupted Drone', 30, 10, 4, '残留データに汚染された整備ドローン。挙動が不安定。', 'A maintenance drone infected with residual data. Erratic and dangerous.', '{"gold":6,"tags":[]}');
INSERT OR REPLACE INTO enemies (enemy_id, name_ja, name_en, hp, attack, defense, description_ja, description_en, rewards_json) VALUES ('f_data_phantom', 'データの亡霊', 'Data Phantom', 45, 13, 3, '消去された記録が形を得たもの。消された記憶を持つ。', 'A deleted record given form. It remembers being erased.', '{"gold":11,"tags":[]}');
